sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
   var haddress = g_form.getValue('u_address_1');
	var hcity = g_form.getValue('u_city');
	var hstate = g_form.getValue('u_state');
	var hzip = g_form.getValue('u_zip_code');
	var hcountry = g_form.getValue('u_country');
	var gri = g_form.getValue('u_yes');
	if (gri == 'false'){
		g_form.setValue('u_paddress_1', haddress);
		g_form.setValue('u_pcity', hcity);
		g_form.setValue('u_pstate', hstate);
		g_form.setValue('u_pcountry', hcountry);
		g_form.setValue('u_pzip_code', hzip);
	}
}
