var Loggedinteacherdetail = Class.create();
Loggedinteacherdetail.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	attendance_info: function() {
		var xyz = '';
// 		gs.info("User name is: {0}", gs.getUserID());
		var user_detail = new GlideRecord('sys_user');
		user_detail.addQuery('sys_id', gs.getUserID());
		user_detail.query();
		if (user_detail.next())
		{
			var abc = this.teachername_info(user_detail.first_name+ " " +user_detail.last_name);
			return abc;
// 			xyz.push (abc);
// 			gs.info("Test 123: {0}", xyz);
// 			return JSON.stringify(xyz);
		}
	},

	teachername_info : function (teachername){
		var tname = new GlideRecord('x_77597_smart_scho_teacher_and_staff_table');
		tname.addQuery('teacher_name', teachername);
		tname.query();
		if (tname.next()){
// 			gs.info("VR: fteacher sys_id: {0}", tname.sys_id);
			return tname.sys_id;
		}

	},

	type: 'Loggedinteacherdetail'
});
