function ifExist(){
	var userCount = 0;
	var usr = new GlideAggregate('x_77597_smart_scho_teacher_and_staff_table');
	usr.addQuery('u_email', current.u_email);
    usr.addAggregate('COUNT','u_email');
// 	usr.groupBy('u_email');
// 	usr.addHaving('COUNT','>', 1);
	usr.query();
	while (usr.next()){
		userCount = usr.getAggregate('COUNT','u_email');
		}
	if (userCount > 0){
		return 'yes'; //duplicate found
	} 
	else {
		return 'no';
}
}

