/**
	 * This function is called when status of the new user record changes to 'Requested'
	 * It fetches the approval record from sysapproval_approver table and updates the DCOnboarding table
	*/
	function updateApprovalRecord (){
		try {
			var getSysId;
			var gr = new GlideRecordSecure('sysapproval_approver');
			gr.addQuery('document_id', this.objnewdc.sys_id);
			gr.query();
			if(gr.next()){
				getSysId = gr.sys_id;
				this.updateApprovalsField(gr.sys_id);
			}

			var getMatchValue = this.checkApprovalFieldValue(this.objnewdc.sys_id, getSysId);
			if(!getMatchValue){
				return false;
			}else {
				return true;
			}
		} catch (error) {
			// var message = error.toString();
			gs.info('IAP: Could not execute updateApprovalRecord function: ',error);
		}
	}

	/**
	 * This function updates the approvals field of the teacher/staff table with
	 * ths sys_id of the sysapproval_approver table
	 * @param {String} apprSysId sys_id of the sysapproval_approver table
	*/
	function updateApprovalsField (apprSysId){
		try {
			var grs = new GlideRecordSecure('x_77597_smart_scho_teacher_and_staff_table');
			grs.addQuery('number', this.objnewdc.number);
			grs.query();
			if(grs.next()){
				grs.approvals = apprSysId;
				grs.update();
			}
		} catch (error) {
			gs.info('SmartSchool: Could not execute updateApprovalsField function: ',error);
		}
		
	}

function checkApprovalFieldValue(sysId, approvalSysId){
		try {
			var keepValue;
			var grp = new GlideRecordSecure('x_77597_smart_scho_teacher_and_staff_table');
			grp.addQuery('sys_id', sysId);
			grp.query();
			if(grp.next()){
				keepValue = grp.approvals;
			}
			if(approvalSysId == keepValue){
				return true;
			}else{
				return false;
			}
		} catch (error) {
			gs.info('SmartSchool: Could not execute _checkApprovalFieldValue function: ',error);
		}
}

